macro.command:
if (args[0].macroPass === "preDamageRoll" && workflow.hitTargets.first() && workflow.item.system.actionType === 'mwak') {
	const itemCharges = macroItem.system.uses.value;
	const newItemCharges = itemCharges - 1;
	if(itemCharges === 0) return;
	let dialog = new Dialog({
	title: `Dipolar Gages`,
	content: `
				<div style="display: flex; align-items: center; justify-content: space-between;">
					<div style="flex: 1;">
						<p>Would you like to use a charge of Dipolar Gages to push your target 15 feet away from you and attempt to knock them prone?<br/><br/>You have <b>${itemCharges}</b> charges available.</p>
					</div>
					<div style="border-left: 1px solid #ccc; padding-left: 10px; text-align: center;">
						<p><b>Time remaining</b></p>
						<p><span id="countdown" style="font-size: 16px; color: red; align: center;">15</span> seconds</p>
					</div>
				</div>`,
		buttons: {
			recover: {
				label: "Continue", callback: async (html) => {
					await MidiQOL.moveTokenAwayFromPoint(workflow.hitTargets.first(), 15, {x: token.center.x, y: token.center.y}, animate = false);
					let content = `You successfully push the creature back 15 feet.`
					let actorPlayer = MidiQOL.playerForActor(actor);
					let chatData = {
					user: actorPlayer.id,
					speaker: ChatMessage.getSpeaker({ token: token }),
					content: content
					};
					ChatMessage.create(chatData);

					const itemData = macroItem.clone(
					{
					name: "Dipolar Gages Save",
					type: "feat",
					effects: [],
					flags: {
						"midi-qol": {
						noProvokeReaction: true,
						onUseMacroName: null,
						forceCEOff: true
						},"midiProperties": {
						nodam: true 
						}
					},
					system: {
						equipped: true,
						actionType: "save",
						save: { dc: 15, ability: "dex", scaling: "flat" },
						components: { concentration: false, material: false, ritual: false, somatic: false, value: "", vocal: false },
						duration: { units: "inst", value: undefined },
						activation: {type: "", cost: null, condition: ""},
						target: {value: null, width: null, units: "", type: "self"}
					},
					},
					{ keepId:true }
					);

					itemData.system.target.type = "self";
					setProperty(itemData.flags, "autoanimations.killAnim", true);
					const itemUpdate = new CONFIG.Item.documentClass(itemData, { parent: workflow.hitTargets.first().actor });
					const options = { showFullCard: false, createWorkflow: true, versatile: false, configureDialog: false, workflowOptions: {autoRollDamage: 'always', autoFastDamage: true} };
					const saveResult = await MidiQOL.completeItemUse(itemUpdate, {}, options);
					if (saveResult.failedSaves.size === 1) {
						const uuid = workflow.hitTargets.first().actor.uuid;
						const hasEffectApplied = await game.dfreds.effectInterface.hasEffectApplied('Prone', uuid);

						if (!hasEffectApplied) {
						await game.dfreds.effectInterface.addEffect({ effectName: 'Prone', uuid });
						}
					}
					await macroItem.update({ "system.uses.value": newItemCharges });
					return;
				}
			},
			cancel: { label: "Cancel", callback: () => false }
		},
			default: "Cancel",
			render: (html) => {
				let timeLeft = 15;
				const countdownElement = html.find("#countdown");
				const timer = setInterval(() => {
					timeLeft--;
					countdownElement.text(timeLeft);
					if (timeLeft <= 0) {
						clearInterval(timer);
						dialog.close();
					}
				}, 1000);

				setTimeout(() => {
					clearInterval(timer);
					if (timeLeft > 0) dialog.close();
				}, timeLeft * 1000);
			}
		});
    dialog.render(true);
};

if(args[0].macroPass === "isAttacked" && workflow.item.system.actionType === "mwak" && workflow.attackTotal < actor.system.attributes.ac.value) {
const itemCharges = macroItem.system.uses.value;
const newItemCharges = itemCharges - 1;
const uuid = actor.uuid;
let chatMessage = game.messages.contents.slice(-2)[0];
chatMessage = chatMessage.flags["midi-qol"].workflowId;
const workflowOrigin = await MidiQOL.Workflow.getWorkflow(chatMessage);

let dialog = new Dialog({
  title: `Dipolar Gages`,
  content: `
            <div style="display: flex; align-items: center; justify-content: space-between;">
                <div style="flex: 1;">
                    <p>Would you like to use your reaction and a charge of Dipolar Gages to attempt to take your attacker's weapon?<br/><br/><b>The weapon must be made of a magnetic metal - Confirm with GM before using ;)</b><br/><br/>You have <b>${itemCharges}</b> charges available.</p>
                </div>
                <div style="border-left: 1px solid #ccc; padding-left: 10px; text-align: center;">
                    <p><b>Time remaining</b></p>
                    <p><span id="countdown" style="font-size: 16px; color: red; align: center;">15</span> seconds</p>
                </div>
            </div>`,
	buttons: {
		recover: {
			label: "Continue", callback: async (html) => {
				const itemData = macroItem.clone(
				{
				name: "Dipolar Gages Save",
				type: "feat",
				effects: [],
				flags: {
					"midi-qol": {
					noProvokeReaction: true,
					onUseMacroName: null,
					forceCEOff: true
					},"midiProperties": {
					nodam: true 
					}
				},
				system: {
					equipped: true,
					actionType: "save",
					save: { dc: 15, ability: "str", scaling: "flat" },
					components: { concentration: false, material: false, ritual: false, somatic: false, value: "", vocal: false },
					duration: { units: "inst", value: undefined },
					activation: {type: "", cost: null, condition: ""},
					target: {value: null, width: null, units: "", type: "self"}
				},
				},
				{ keepId:true }
				);
				itemData.system.target.type = "self";
				setProperty(itemData.flags, "autoanimations.killAnim", true);
				const itemUpdate = new CONFIG.Item.documentClass(itemData, { parent: workflowOrigin.actor });
				const options = { showFullCard: false, createWorkflow: true, versatile: false, configureDialog: false, workflowOptions: {autoRollDamage: 'always', autoFastDamage: true} };
                const saveResult = await MidiQOL.completeItemUse(itemUpdate, {}, options);

				if (saveResult.failedSaves.size === 1) {
					await game.dfreds.effectInterface.addEffect({ effectName: 'Reaction', uuid });
					await workflowOrigin.item.update({ "system.equipped": false });
					let content = `Your ${workflowOrigin.item.name} is taken from your hands by ${actor.name}.`
					let actorPlayer = MidiQOL.playerForActor(workflowOrigin.actor);
					let chatData = {
					user: actorPlayer.id,
					speaker: ChatMessage.getSpeaker({ token: workflowOrigin.token }),
					content: content
					};
					ChatMessage.create(chatData);
				}
				await macroItem.update({ "system.uses.value": newItemCharges });
				return;
			}
		},
		cancel: { label: "Cancel", callback: () => false }
	},
        default: "Cancel",
        render: (html) => {
            let timeLeft = 15;
            const countdownElement = html.find("#countdown");
            const timer = setInterval(() => {
                timeLeft--;
                countdownElement.text(timeLeft);
                if (timeLeft <= 0) {
                    clearInterval(timer);
                    dialog.close();
                }
            }, 1000);

            setTimeout(() => {
                clearInterval(timer);
                if (timeLeft > 0) dialog.close();
            }, timeLeft * 1000);
        }
    });
    dialog.render(true);
}
