macro.command:
if(args[0].macroPass === "templatePlaced") {
    if(!game.modules.get("effectmacro")?.active) return;
    async function wait(ms) { return new Promise(resolve => { setTimeout(resolve, ms); }); }
    const targetsSet = workflow.targets;
    const targetsArray = Array.from(targetsSet);
    const excludedTypes = ["undead", "construct"];
    const actorProf = actor.system.attributes.prof;

    const validTargetsArray = targetsArray.filter(target => {
        const creatureType = MidiQOL.raceOrType(target);
        return !excludedTypes.includes(creatureType);
    });
    console.log(validTargetsArray, "validTargetArray")

    for (validTarget of validTargetsArray) {
    const tempEffectData = [{
        "icon": macroItem.img,
        "name": macroItem.name,
        "origin": macroItem.uuid,
        "disabled": false,
        "duration": {
            "rounds": 1
        },
        "flags": {
            "effectmacro": {
                "onCreate": {
                    "script": `
                        const validTarget = await fromUuid('${validTarget.document.uuid}');

                        let effectBlinded = validTarget.actor.appliedEffects.find(e => e.name === "Blinded");
                        let effectDeafened = validTarget.actor.appliedEffects.find(e => e.name === "Deafened");
                        let effectParalyzed = validTarget.actor.appliedEffects.find(e => e.name === "Paralyzed");
                        let effectPoisoned = validTarget.actor.appliedEffects.find(e => e.name === "Poisoned");

                        let buttons = [
                            {
                                action: "Healing",
                                label: "Healing",
                                callback: async () => {
                                    const targetMaxHp = validTarget.actor.system.attributes.hp.max;
                                    const targetCurrentHp = validTarget.actor.system.attributes.hp.value;
                                    let healRoll = await new CONFIG.Dice.DamageRoll("3d8 + ${actorProf}", {}, {type: "healing", properties: ["mgc"]}).evaluate();
                                    await MidiQOL.displayDSNForRoll(healRoll, 'damageRoll')
                                    let healTotal = healRoll.total;

                                    const itemData = {
                                        name: "Healing Glyph",
                                        type: "feat",
                                        img: "${macroItem.img}"
                                    }
                                    await new MidiQOL.DamageOnlyWorkflow(validTarget.actor, validTarget.token, healRoll.total, "healing", token ? [validTarget.token] : [], healRoll, {itemData: itemData, flavor: "Healing Glyph"});
                                    new Sequence()

                                    .effect()
                                    .file("jb2a.particle_burst.01.circle.green")
                                    .attachTo(token)
                                    .scaleToObject(3)
                                    .opacity(1)
                                    .zIndex(0)
                                    .belowTokens(false)
                                    .fadeIn(1500)
                                    .fadeOut(1500)

                                    .play()

                                    let displayHp = healTotal;
                                    if((healTotal + targetCurrentHp) > targetMaxHp) displayHp = targetMaxHp - targetCurrentHp;

                                    let content = \`Healing Glyph heals you for \` + displayHp + \` hit points.\`;
                                    let actorPlayer = MidiQOL.playerForActor(validTarget.actor);
                                    let chatData = {
                                    user: actorPlayer.id,
                                    speaker: ChatMessage.getSpeaker({ token: validTarget }),
                                    content: content
                                    };
                                    ChatMessage.create(chatData);
                                }
                            }
                        ];

                        // Dynamically add buttons based on conditions
                        if (effectBlinded) {
                            buttons.push({
                                action: "Restoration - Blinded",
                                label: "Restoration - Blinded",
                                callback: async () => {
                                    await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: validTarget.actor.uuid, effects: [effectBlinded.id] });
                                    content = "Healing Glyph removes the Blinded Effect";
                                    let actorPlayer = MidiQOL.playerForActor(validTarget.actor);
                                    let chatData = {
                                    user: actorPlayer.id,
                                    speaker: ChatMessage.getSpeaker({ token: validTarget }),
                                    content: content
                                    };
                                    ChatMessage.create(chatData);
                                }
                            });
                        }

                        if (effectDeafened) {
                            buttons.push({
                                action: "Restoration - Deafened",
                                label: "Restoration - Deafened",
                                callback: async () => {
                                    await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: validTarget.actor.uuid, effects: [effectDeafened.id] });
                                    content = "Healing Glyph removes the Deafened Effect";
                                    let actorPlayer = MidiQOL.playerForActor(validTarget.actor);
                                    let chatData = {
                                    user: actorPlayer.id,
                                    speaker: ChatMessage.getSpeaker({ token: validTarget }),
                                    content: content
                                    };
                                    ChatMessage.create(chatData);
                                }
                            });
                        }

                        if (effectParalyzed) {
                            buttons.push({
                                action: "Restoration - Paralyzed",
                                label: "Restoration - Paralyzed",
                                callback: async () => {
                                    await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: validTarget.actor.uuid, effects: [effectParalyzed.id] });
                                    content = "Healing Glyph removes the Paralyzed Effect";
                                    let actorPlayer = MidiQOL.playerForActor(validTarget.actor);
                                    let chatData = {
                                    user: actorPlayer.id,
                                    speaker: ChatMessage.getSpeaker({ token: validTarget }),
                                    content: content
                                    };
                                    ChatMessage.create(chatData);
                                }
                            });
                        }

                        if (effectPoisoned) {
                            buttons.push({
                                action: "Restoration - Poisoned",
                                label: "Restoration - Poisoned",
                                callback: async () => {
                                    await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: validTarget.actor.uuid, effects: [effectPoisoned.id] });
                                    content = "Healing Glyph removes the Poisoned Effect";
                                    let actorPlayer = MidiQOL.playerForActor(validTarget.actor);
                                    let chatData = {
                                    user: actorPlayer.id,
                                    speaker: ChatMessage.getSpeaker({ token: validTarget }),
                                    content: content
                                    };
                                    ChatMessage.create(chatData);
                                }
                            });
                        }
                        await foundry.applications.api.DialogV2.wait({
                            window: { title: '${validTarget.actor.name} | Healing Glyph' },
                            content: \`
                                <div class='gps-dialog-container'>
                                    <div class='gps-dialog-section'>
                                        <div class='gps-dialog-content'>
                                            <div>
                                                <div class='gps-dialog-flex'>
                                                    <p class='gps-dialog-paragraph'>How would you like to use the Healing Glyph effect?</p>
                                                    <div id='image-container' class='gps-dialog-image-container'>
                                                        <img src='${macroItem.img}' class='gps-dialog-image'>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            \`,
                            buttons: buttons,
                            default: "healing"
                        });
                    `
                }
            }
        }
    }];

    await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: validTarget.actor.uuid, effects: tempEffectData });
    await wait(100);
    let tempEffectRemoveOpp = validTarget.actor.appliedEffects.find(i => i.name.toLowerCase() === "healing glyph");
    await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: validTarget.actor.uuid, effects: [tempEffectRemoveOpp.id] });
    }
}
