macro.command:
const target = workflow.targets.first();
if (!target) return ui.notifications.warn("No target selected.");

actor = target.actor;
const actorClasses = actor.classes ?? {};
const classKeys = Object.keys(actorClasses);

let totalHitDiceSelected = 0;

const totalHitDice = classKeys.reduce((acc, key) => {
  const cls = actorClasses[key];
  const levels = parseInt(cls.system.levels) || 0;
  const spent  = parseInt(cls.system.hd?.spent) || 0;
  return acc + Math.max(levels - spent, 0);
}, 0);

if (totalHitDice === 0) {
  return ui.notifications.warn("You have no hit dice remaining.");
}

let content = `<p>Select the number of hit dice to use:</p><form>`;
for (const key of classKeys) {
  const cls = actorClasses[key];
  const levels = parseInt(cls.system.levels) || 0;
  const spent  = parseInt(cls.system.hd?.spent) || 0;
  const hitDiceAvailable = Math.max(levels - spent, 0);
  if (hitDiceAvailable === 0) continue;

  const denom = cls.system.hd?.denomination ?? "d8";
  const dieLabel = String(denom).startsWith("d") ? String(denom) : `d${denom}`;

  content += `
  <div class="form-group">
    <label style="flex: 0 0 auto; margin: 0 10px; text-align: left;">
      ${cls.name} - Available: <b>${hitDiceAvailable}${dieLabel}</b>
    </label>
    <div class="form-fields">
      <button type="button" class="hit-dice-decrement" data-key="${key}">-</button>
      <span id="hitDiceDisplay-${key}" style="flex: 0 0 auto; margin: 0 10px; text-align: center;">0</span>
      <button type="button" class="hit-dice-increment" data-key="${key}">+</button>
    </div>
  </div>`;
}
content += `</form>`;

await foundry.applications.api.DialogV2.wait({
  window: { title: `Use Hit Dice` },
  content,
  buttons: [
    {
      action: "continue",
      label: "Continue",
      callback: async (event, _button, dialog) => {
        for (const key of classKeys) {
            const display = dialog.element.querySelector(`#hitDiceDisplay-${key}`);
            if (!display) continue;

            const hitDiceToUse = parseInt(display?.textContent ?? "0");
            const hitDiceRollType = actorClasses[key].system.hd.denomination;

            if (!hitDiceToUse || hitDiceToUse === 0) {
                continue;
            }

            for (let i = 0; i < hitDiceToUse; i++) {
                await actor.rollHitDie({ denomination: hitDiceRollType });
            }
        }
      }
    },
    { action: "cancel", label: "Cancel", callback: () => false }
  ],
  default: "cancel",
  render: (event) => {
    const root = event.target?.element;
    if (!root) return;

    root.addEventListener('click', ev => {
      const t = ev.target;
      const isInc = t.classList.contains('hit-dice-increment');
      const isDec = t.classList.contains('hit-dice-decrement');
      if (!isInc && !isDec) return;

      const key = t.dataset.key;
      const span = root.querySelector(`#hitDiceDisplay-${key}`);
      if (!span) return;

      let currentValue = parseInt(span.textContent) || 0;

      if (isInc) {
        if (totalHitDiceSelected < 1) {
          span.textContent = String(currentValue + 1);
          totalHitDiceSelected++;
        }
      } else if (isDec && currentValue > 0) {
        span.textContent = String(currentValue - 1);
        totalHitDiceSelected--;
      }
    });
  }
});
