macro.command:
if(args[0].macroPass === "preAttackRoll") {
    if(macroItem.system.uses.value === 0) return;
    let target = workflow.hitTargets.first();
    if(!target.document.hasStatusEffect("frightened")) return;

    await foundry.applications.api.DialogV2.wait({
        window: { title: 'Impetus Slasher' },
        content: `
            <div class="gps-dialog-container">
                <div class="gps-dialog-section">
                    <div class="gps-dialog-content">
                        <div>
                            <div class="gps-dialog-flex">
                                <p class="gps-dialog-paragraph">Would you like to use Impetus Slasher to give advantage on this attack? If the attack hits, you gain critical and bonus damage.</p>
                                <div id="image-container" class="gps-dialog-image-container">
                                    <img src="${macroItem.img}" class="gps-dialog-image">
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        `,
        buttons: [{
            action: "Yes",
            label: "Yes",
            callback: async (event, button, dialog) => {
                workflow.advantage = true;

                let effectData = actor.appliedEffects.find(e => e.name === "Impetus Slasher");
                await effectData.setFlag("gambits-premades", "impetusSlasherUsed", true);
            }
        },
        {
            action: "No",
            label: "No",
            callback: async (event, button, dialog) => {return;}
        }],
        close: async (event, dialog) => {
            return;
        }, rejectClose:false
    });
}

if(args[0].macroPass === "preDamageRoll") {
    if(macroItem.system.uses.value === 0) return;
    let target = workflow.hitTargets.first();
    if(!target.document.hasStatusEffect("frightened")) return;

    let effectData = actor.appliedEffects.find(e => e.name === "Impetus Slasher");
    let effectAdv = effectData.getFlag("gambits-premades", "impetusSlasherUsed");
    if(effectAdv) {
        workflow.isCritical = true;
    }
}

if(args[0].macroPass === "postDamageRollComplete") {
    if(macroItem.system.uses.value === 0) return;
    let target = workflow.hitTargets.first();
    if(!target.document.hasStatusEffect("frightened")) return;

    let effectData = actor.appliedEffects.find(e => e.name === "Impetus Slasher");
    let effectAdv = effectData.getFlag("gambits-premades", "impetusSlasherUsed");
    if(effectAdv) {
        let damageRoll = await new CONFIG.Dice.DamageRoll(`2d12`).evaluate();
        await MidiQOL.displayDSNForRoll(damageRoll, 'damageRoll');

        const itemData = {
            name: "Impetus Slasher - Damage (Force)",
            type: "feat",
            img: macroItem.img
        }

        new MidiQOL.DamageOnlyWorkflow(actor, token, damageRoll.total, "force", target ? [target] : [], damageRoll, {itemData: itemData});

        await macroItem.update({"system.uses.value": macroItem.system.uses.value - 1});
        await effectData.unsetFlag("gambits-premades", "impetusSlasherUsed");
    }
}
