macro.command:
if(args[0].macroPass === "postDamageRoll") {
    if(workflow.hitTargets.first()) {
        const gridSize = canvas.grid.size;
        const scaleFactor = gridSize / 100;

        let source = token;
        let targets = workflow.hitTargets;

        // just in case the user hasn't targetted a token, let's add a quick warning
        if (!targets || targets?.length === 0) {
            return ui.notifications.warn("Please target at least one token")
        }

        const enableTrail = true;
        const enableImpact = false;
        const enableSound = false;
        const enableSwitchDistance = false;
        const enableReturn = false;
        const enableBlood = true;
        const enableShake = false;
        const debug = false;

        const weaponGroup = 'melee_attack.01';
        const weapon = 'shortsword.01';
        const trail = 'trail.01';
        const color = 'pinkpurple';
        const impact = 'jb2a.impact.004.yellow';
        const impactScale = 1.5;
        const soundFileMelee = "modules/dnd5e-animations/assets/sounds/Weapons/Melee_Bludgeoning/bludgeoning-swing-impact-blunt-1.mp3";
        const soundFileRange = "modules/dnd5e-animations/assets/sounds/Weapons/Melee_Bludgeoning/bludgeoning-swing-impact-blunt-1.mp3";
        const delaySound = 300;
        const switchDistanceFt = 5;
        const range = "";
        const returnFile = "";
        const delayBetweenAttacks = 1000;

        function log(string, variable){
            return console.log(`%c ${string}`, 'color: #bada55', variable)
        }



        // Let's reconstruct the database path and fetch the array of all the variations for the chosen weapon
        const dbPath = `jb2a.${weaponGroup}.${weapon}`;
        const entries = Sequencer.Database.getEntry(dbPath) ?? null;

        // The length of the array will represent how many entries there are, 
        // which we'll use later on to randomly pick the appropriate one.
        const entriesLength = entries.length;

        if(debug){
            log("source: ", source);
            log("targets: ", targets);
            log("weapon group: ", weaponGroup);
            log("weapon: ", weapon);
            log("trail: ", trail);
            log("color: ", color);
            log("impact: ", impact);
            log("soundFileMelee: ", soundFileMelee);
            log("soundFileRange: ", soundFileRange);
            log("enableBlood: ", enableBlood);
            log("enableSound: ", enableSound);
            log("enableShake: ", enableShake);
            log("enableTrail: ", enableTrail);
            log("dbPath: ", dbPath);
            log("entries: ", entries);
            log("entries length: ", entriesLength);
        }


        /******************************************
         * MAIN ANIMATION FUNCTIONS INITIALIZATION*
         * ****************************************/

        async function meleeAttack(target, randMelee, randTrail, impact, isMirrored, targetScale, within5ft) {
            const sourceScale = { x: source?.document?.texture?.scaleX ?? 1, y: source?.document?.texture?.scaleY ?? 1 }

            // Here is some Maths that we use for the "Shake" animation of the targets, move along... nothing to see here :D
            const amplitude = Sequencer.Helpers.random_float_between(0.0, 0.2);
            let hitRay = new Ray(source, target);
            const shakeDirection = { x: Math.sign(hitRay.dx), y: Math.sign(hitRay.dy) };
            const values = {
                x: [0, -amplitude * shakeDirection.y, amplitude * shakeDirection.y, (-amplitude * shakeDirection.y) / 4, (amplitude * shakeDirection.y) / 4, 0],
                y: [0, amplitude * shakeDirection.x, -amplitude * shakeDirection.x, (amplitude * shakeDirection.x) / 4, (-amplitude * shakeDirection.x) / 4, 0]
            }
            const interval = 50;
            const easeOption = "easeInOutSine";

            if(debug){
                log("THIS IS A: ", "MELEE ATTACK")
                log("within 5 feet?: ", within5ft)
                log("randow melee: ", randMelee)
                log("random trail: ", randTrail)
                log("target scale: ", targetScale)
                log("source scale: ", sourceScale)
            }

            // Below is the Sequencer code for all the animations
            // The wiki for Sequencer is the best resource to learn about it: 
            // https://fantasycomputer.works/FoundryVTT-Sequencer/#/
            // check it out, follow the tutorials from Wasp, the developer, everything is there!
            new Sequence()

                .effect()
                .from(source)
                .scale({ x: sourceScale.x, y: sourceScale.y })
                .anchor(0.5)
                .rotate(0)
                .rotateTowards(target, {rotationOffset: -90})
                .duration(1500)
                .fadeOut(500)
                .zIndex(5)
                .playIf(within5ft)

                .effect()
                .from(source)
                .scale({ x: sourceScale.x, y: sourceScale.y })
                .anchor(0.5)
                .rotate(0)
                .rotateTowards(target, {rotationOffset: -90})
                .animateProperty("sprite", "position.y", { from: 0, to: hitRay.distance -gridSize, duration: 500+hitRay.distance, ease: "easeOutQuint"})
                .duration(1500)
                .fadeOut(500)
                .zIndex(5)
                .playIf(!within5ft)

                .animation()
                .on(source)
                .fadeOut(50)

                .effect()
                .file("jb2a.gust_of_wind.veryfast")
                .atLocation(source, {cacheLocation: true})
                .stretchTo(target)
                .randomizeMirrorY()
                .belowTokens()
                .playIf(!within5ft)

                .effect()
                .file(`${randTrail}`)
                .atLocation(target)
                .rotateTowards(source)
                .rotate(180)
                .animateProperty("sprite", "position.x", { from: -(2.5*gridSize + hitRay.distance), to: -2.5*gridSize, duration: 500+hitRay.distance, ease: "easeOutQuint"})
                .scale(1)
                .mirrorY(isMirrored)
                .zIndex(11)
                .playbackRate(0.9)
                .playIf(enableTrail)

                .effect()
                .file(`${randMelee}`)
                .atLocation(target)
                .rotateTowards(source)
                .rotate(180)
                .animateProperty("sprite", "position.x", { from: -(2.5*gridSize + hitRay.distance), to: -2.5*gridSize, duration: 500+hitRay.distance, ease: "easeOutQuint"})
                .scale(1)
                .mirrorY(isMirrored)
                .zIndex(10)
                .playbackRate(0.9)
                .waitUntilFinished(-1000) // By design, The hit should always be 1 second from the end of the weapon attack and the trail animations


                .animation()
                .on(source)
                .fadeIn(500)

                .sound()
                .file(soundFileMelee)
                .playIf(enableSound)

                .effect()
                .file(`${impact}`)
                .atLocation(target)
                .scaleToObject(impactScale, { uniform: true })
                .zIndex(12)
                .playbackRate(0.9)
                .playIf(enableImpact)

                //START - BLOOD SPLATTER EFFECT
                .effect()
                .file('jb2a.liquid.splash_side.red')
                .atLocation(target)
                .rotateTowards(source)
                .randomRotation()
                .scaleToObject(1.5, { uniform: true })
                .playIf(enableBlood)
                .zIndex(12)
                //END - BLOOD SPLATTER EFFECT

                // START OF SHAKE SECTION
                .animation()
                .on(target)
                .fadeOut(50)
                .playIf(enableShake)

                .effect()
                .from(target)
                .loopProperty("spriteContainer", "position.x", {
                    values: values.x,
                    duration: interval - ((interval * amplitude) / 2),
                    gridUnits: true,
                    ease: easeOption
                })
                .loopProperty("spriteContainer", "position.y", {
                    values: values.y,
                    duration: interval - ((interval * amplitude) / 2),
                    gridUnits: true,
                    ease: easeOption
                })
                .scale({ x: targetScale.x, y: targetScale.y })
                .duration(interval * 9)
                .playIf(enableShake)
                .zIndex(1)
                .waitUntilFinished(-150)

                .animation()
                .on(target)
                .fadeIn(50)
                .playIf(enableShake)
                // END OF SHAKE SECTION

                .play();
        };

        async function rangedAttack(target, targetScale) {
            if(debug){
                log("THIS IS A: ", "RANGED ATTACK")
                log("target scale: ", targetScale)
            }

            const amplitude = Sequencer.Helpers.random_float_between(0.0, 0.2);
            let hitRay = new Ray(source, target);
            const shakeDirection = { x: Math.sign(hitRay.dx), y: Math.sign(hitRay.dy) };
            const values = {
                x: [0, -amplitude * shakeDirection.y, amplitude * shakeDirection.y, (-amplitude * shakeDirection.y) / 4, (amplitude * shakeDirection.y) / 4, 0],
                y: [0, amplitude * shakeDirection.x, -amplitude * shakeDirection.x, (amplitude * shakeDirection.x) / 4, (-amplitude * shakeDirection.x) / 4, 0]
            }
            const interval = 50;
            const easeOption = "easeInOutSine";

            new Sequence()

                .sound()
                .file(soundFileRange)
                .playIf(enableSound)
                .delay(delaySound)

                .effect()
                .file(range)
                .atLocation(source)
                .stretchTo(target)
                .waitUntilFinished(-800)
                .zIndex(10)

                .effect()
                .file(`${impact}`)
                .atLocation(target)
                .scaleToObject(1.2, { uniform: true })
                .zIndex(12)
                .playIf(enableImpact)

                .effect()
                .file(returnFile)
                .atLocation(source)
                .stretchTo(target)
                .zIndex(10)
                .playIf(enableReturn)

                //START - BLOOD SPLATTER EFFECT
                .effect()
                .file('jb2a.liquid.splash_side.red')
                .atLocation(target)
                .rotateTowards(source)
                .randomRotation()
                .scaleToObject(1.5, { uniform: true })
                .zIndex(11)
                .playIf(enableBlood)
                //END - BLOOD SPLATTER EFFECT

                // START OF SHAKE SECTION
                .animation()
                .on(target)
                .fadeOut(50)
                .playIf(enableShake)

                .effect()
                .from(target)
                .loopProperty("spriteContainer", "position.x", {
                    values: values.x,
                    duration: interval - ((interval * amplitude) / 2),
                    gridUnits: true,
                    ease: easeOption
                })
                .loopProperty("spriteContainer", "position.y", {
                    values: values.y,
                    duration: interval - ((interval * amplitude) / 2),
                    gridUnits: true,
                    ease: easeOption
                })
                .scale({ x: targetScale.x, y: targetScale.y })
                .duration(interval * 9)
                .playIf(enableShake)
                .zIndex(1)
                .waitUntilFinished(-150)

                .animation()
                .on(target)
                .fadeIn(50)
                .playIf(enableShake)
                // END OF SHAKE SECTION

                .play()
        }

        /**************************
         * ANIMATION CALL AND LOOP*
         **************************/

        // We will loop through this code for each targeted token and 
        // pick a different random number each loop in order to have more
        // probability to pick a different animation everytime
        for (let target of targets) {


            let targetScale = { x: target?.document?.texture?.scaleX ?? 1, y: target?.document?.texture?.scaleY ?? 1 }
            // Arrays start at 0. This will return a random integer which we'll use to pick a random attack variation
            // and its corresponding trail.
            //let rand = Sequencer.Helpers.random_int_between(0, entriesLength - 1);
            let rand = Math.floor(Math.random() * ((entriesLength -1) + 1))
            // Here, we rebuild the database paths and pass them along to the animation function.
            let randMelee = `${dbPath}.${rand}`;
            let randTrail;
            enableTrail? randTrail =`jb2a.${weaponGroup}.${trail}.${color}.${rand}` : randTrail = 'jb2a.antilife_shell.blue_no_circle'
            //let randTrail = `jb2a.${weaponGroup}.${trail}.${color}.${rand}`;

            // Let's add to the randomisation by mirroring the animation half the time, on top of the random attack variation.
            let isMirrored = Math.random() < 0.5; // 50% probability. 0.1 would make it 10%, 0.2 20%...etc

            // let switchDistanceFt = switchDistanceFt ?? 1000; // Placeholder
            const targetBounds = target.bounds.pad(gridSize * (switchDistanceFt / 5 - 1 + 0.5), gridSize * (switchDistanceFt / 5 - 1 + 0.5));
            const sourceBounds = source.bounds;
            const within5ft = (target.bounds.pad(gridSize * (0.5), gridSize * (0.5))).intersects(sourceBounds);
            const withinSwitchDistance = targetBounds.intersects(sourceBounds);

            // This is the code which actually calls the animation function.
            if (withinSwitchDistance || !enableSwitchDistance) {
                await meleeAttack(target, randMelee, randTrail, impact, isMirrored, targetScale, within5ft)
                await Sequencer.Helpers.wait(delayBetweenAttacks)
            }
            else {
                await rangedAttack(target, targetScale)
                await Sequencer.Helpers.wait(delayBetweenAttacks)
            }

            if(debug){
                log("random integer: ", rand)
                log("randow melee: ", randMelee)
                log("random trail: ", randTrail)
                log("target scale: ", targetScale)
                log("switch Distance", switchDistanceFt)
                log("within switch distance: ", withinSwitchDistance)
            }
        }
    }

    if(workflow.attackRoll.dice[0].total !== 20) return;
    let target = workflow.hitTargets.first();
    new Sequence()
    .effect()
        .file("jb2a.impact.003.dark_purple")
        .fadeIn(500)
        .fadeOut(500)
        .atLocation(target)
        .scaleToObject(4)
    .play()

    let hasGloom = actor.items.some(i => i.name === "Gloom" && i.system.equipped && i.system.attuned);
    let numDice = hasGloom ? 6 : 4;
    let damageRoll = await new CONFIG.Dice.DamageRoll(`${numDice}d8`, {}, {type: "necrotic", properties: ["mgc"]}).evaluate();
    await MidiQOL.displayDSNForRoll(damageRoll, 'damageRoll');

    const itemData = {
        name: "Doom - Damage (Necrotic)",
        type: "feat",
        img: macroItem.img
    }

    await new MidiQOL.DamageOnlyWorkflow(actor, token, damageRoll.total, "necrotic", [target], damageRoll, {itemData: itemData});
}
