macro.command:
if(args[0].macroPass === "preDamageRoll") {
	const target = workflow.hitTargets.first();
	if(target)
	{
		const actorSize = target.actor.system.traits.size;
		if(actorSize === "med" || actorSize === "sm" || actorSize === "tiny")
		{
			let effectData = [{
                disabled: false,
                name: "Tentacles - Grappled",
                duration: {
                	seconds: 60
                },
                transfer: false,
                icon: "systems/dnd5e/icons/svg/statuses/grappled.svg",
                origin: macroItem.uuid,
				changes: [
					{
						key: 'system.attributes.movement.all',
						mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
						value: '0',
						priority: 25,
					},
				]
            }];
            await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: target.actor.uuid, effects: effectData });

			const spellDC = 15;
			let saveAbility = "int";
			const skillCheck = await target.actor.rollAbilitySave(saveAbility);
			if (skillCheck.total < spellDC) {
				let effectDataStunned = [{
					disabled: false,
					name: "Tentacles - Stunned",
					duration: {
						seconds: 60
					},
					transfer: false,
					icon: "systems/dnd5e/icons/svg/statuses/stunned.svg",
					origin: macroItem.uuid,
					changes: [
						{
							key: `flags.midi-qol.fail.ability.save.dex`,
							mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
							value: '1',
						},
						{
							key: `flags.midi-qol.fail.ability.save.str`,
							mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
							value: '1',
						},
						{
							key: `flags.midi-qol.grants.advantage.attack.all`,
							mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM,
							value: '1',
						},
					]
				}];
				await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: target.actor.uuid, effects: effectDataStunned });
			}
		}
	}
}
else if(args[0] === "off") {
	let effectData = actor.effects.find(e => e.name === "Tentacles - Grappled");
	let effectDataStunned = actor.effects.find(e => e.name === "Tentacles - Stunned");

	if (effectData) {  
		await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: actor.uuid, effects: effectData.id });
	}
	if (effectDataStunned) {
		await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: actor.uuid, effects: effectDataStunned.id });
	}
}
else if(args[0] === "each") {
	const spellDC = 15;
	const hasEffectApplied = actor.appliedEffects.find(e => e.name === "Tentacles - Grappled")
	const hasEffectAppliedStun = actor.appliedEffects.find(e => e.name === "Tentacles - Stunned")
	if(hasEffectApplied && !hasEffectAppliedStun)
	{
		let dialogId = "tentacles";
		let dialogTitlePrimary = `${token.actor.name} | ${item.name}`;
		let browserUser = MidiQOL.playerForActor(token.actor);
		if (!browserUser.active) {
			browserUser = game.users?.activeGM;
		}
		let dialogContent = `
			<div class="gps-dialog-container">
				<div class="gps-dialog-section">
					<div class="gps-dialog-content">
						<p class="gps-dialog-paragraph">Would you like to use your action to make an ability check to escape being grappled by Tentacles?</p>
						<div>
							<div class="gps-dialog-flex">
								<table style="background-color: rgba(181, 99, 69, 0.2);" width="100%"><tbody><tr><th>Athletics</th><th>Acrobatics</th></tr><tr><td style="text-align: center;vertical-align: middle;"><input type="radio" value="ath" id="ability-check" name="ability-check" style="margin: 0 auto;"></td><td style="text-align: center;vertical-align: middle;"><input type="radio" value="acr" id="ability-check" name="ability-check" style="margin: 0 auto;"></td></tr></tbody></table>
								<div id="image-container" class="gps-dialog-image-container">
									<img id="img_${dialogId}" src="${item.img}" class="gps-dialog-image">
								</div>
							</div>
						</div>
					</div>
				</div>
				<div class="gps-dialog-button-container">
					<button id="pauseButton_${dialogId}" type="button" class="gps-dialog-button">
						<i class="fas fa-pause" id="pauseIcon_${dialogId}" style="margin-right: 5px;"></i>Pause
					</button>
				</div>
			</div>
		`;

		let result = await game.gps.socket.executeAsUser("process3rdPartyReactionDialog", browserUser.id, {dialogTitle:dialogTitlePrimary,dialogContent,dialogId,initialTimeLeft: 30,validTokenPrimaryUuid: token.document.uuid,source:browserUser.isGM ? "gm" : "user",type:"singleDialog"});

		const { userDecision, enemyTokenUuid, allyTokenUuid, damageChosen, abilityCheck, source, type } = result;

		if (!userDecision) {
			return;
		}
		else if (userDecision) {
			if (abilityCheck) {
				const skillCheck = await actor.rollSkill(abilityCheck);
				if (skillCheck.total >= spellDC) {
					let effectData = actor.effects.find(e => e.name === "Tentacles");

					if (effectData) {  
						await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: actor.uuid, effects: effectData.id });
					}
				}
			}
		}
	}
}
